import { faker } from '@faker-js/faker';



// use a randomly selected row from the appointments table to create encounters
export function createObservations(code, patIds) {

    return {
        obs_code: code,
        pat_id: faker.helpers.arrayElement(patIds),
        interface: 'MIE DataFaker',

        // the following fields are defined as NOT NULL and have no default value
        template_id: '',
        obs_result: faker.lorem.sentence(),
        obs_name: faker.animal.type(),
        test_comments: faker.lorem.sentence(),
        free_text: faker.lorem.sentence(),
        micro_result: faker.lorem.sentence(),
        interpretive_text: faker.lorem.sentence()

    }
};
