import connection from './connection.js';


export default async function getAppointmnets(dbConnection) {
    if (dbConnection) {
        return new Promise((resolve, reject) => {
            connection.query('SELECT * FROM appointments', (err, results) => {
                if (err) {
                    console.error(`Error querying the database: ${err.message}`);
                    reject(err)
                } else {
                    resolve(results);
                }
            });
        });
    } else {
        console.error('Database connection required for appointment retrieval');
    }
};
