import connection from './connection.js';


export default async function getPatIds(dbConnection) {
    if (dbConnection) {
        return new Promise((resolve, reject) => {
            connection.query('SELECT pat_id FROM patients', (err, results) => {
                if (err) {
                    console.error(`Error querying the database: ${err.message}`);
                    reject(err)
                } else {
                    resolve(results.map(id => id.pat_id));
                }
            });
        });
    } else {
        console.error('Database connection required for patient id retrieval');
    }   
};
